package p2;


// Bean class
// or 
// POJO or 
// Entity
// or 
// DTO

public class Student {

	private int age;
	private int marks;
	private int id;
	private String name;
	
	
	
	
	public Student() {
		super();
		// TODO Auto-generated constructor stub
	}
	public int getAge() {
		return age;
	}
	public void setAge(int age) {
		this.age = age;
	}
	public int getMarks() {
		return marks;
	}
	public void setMarks(int marks) {
		this.marks = marks;
	}
	public int getId() {
		return id;
	}
	public void setId(int id) {
		this.id = id;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	
	
		
	
	
}//end class

